library(shiny)

# Define UI
ui <- fluidPage(
  titlePanel("Demo Shiny App"),
  sidebarLayout(
    sidebarPanel(
      sliderInput("slider", "Select a number:", min = 1, max = 100, value = 50)
    ),
    mainPanel(
      textOutput("result")
    )
  )
)

# Define Server
server <- function(input, output) {
  output$result <- renderText({
    paste("You selected:", input$slider)
  })
}

# Run App
shinyApp(ui = ui, server = server)
